            '42',
            '');

      $task = new ManiphestTask();

      $tasklink = "";
      if (!empty($taskEdge)) {
        foreach ($taskEdge as $key => $value) {
          $taskPHID = $key;
          $task = id(new ManiphestTask())->loadOneWhere("phid = '".$key."'");
          if ($task !== null) {
            // 任务的链接
            $tasklink = phutil_tag(
             'a',
             array(
              'href' => "/T".$task->getID(),
              'class' => 'phui-oi-link',
              'title' => $task->getTitle(),
             ),
             $task->getTitle());
            // 这里只要最新的一个任务
            break;
          }
        }
      }

      $id = $project->getID();

      $icon = $project->getDisplayIconIcon();
      $icon_icon = id(new PHUIIconView())
        ->setIcon($icon);

      $icon_name = $project->getDisplayIconName();

      // 这里加上任务数量和最新的任务链接
      $item = id(new PHUIObjectItemView())
        ->setHeader($project->getName())
        ->setHref("/project/view/{$id}/")
        ->setImageURI($project->getProfileImageURI())
        ->addAttribute(
          array(
            $icon_icon,
            ' ',
            $icon_name,
            ' ',
            count($taskEdge),
            ' ',
            $tasklink,
          ));

      if ($project->getStatus() == PhabricatorProjectStatus::STATUS_ARCHIVED) {
        $item->addIcon('fa-ban', pht('Archived'));
        $item->setDisabled(true);
      }

      if ($this->showMember) {
        $is_member = $project->isUserMember($viewer_phid);
        if ($is_member) {
          $item->addIcon('fa-user', pht('Member'));
        }
      }

      if ($this->showWatching) {
        $is_watcher = $project->isUserWatcher($viewer_phid);
        if ($is_watcher) {
          $item->addIcon('fa-eye', pht('Watching'));
        }
      }

      $list->addItem($item);
    }

    return $list;
  }
```

